mapper.select:
SELECT 
	contentSeq, contentType, title, exposure, contents, url, filePath, fileName,
	DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') regdate, DATE_FORMAT(moddate,'%Y-%m-%d %H:%i:%s') moddate
FROM TB_CONTENTS
WHERE 1=1
	AND contentType = #{contentType}

	AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
	

	AND title like concat('%',  #{keyword}, '%')  
	
ORDER BY regdate DESC
LIMIT #{startIdx}, #{rowCount}

SELECT count(contentSeq) cnt
FROM TB_CONTENTS
WHERE 1=1
	AND contentType = #{contentType}

	AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}


	AND title like concat('%',  #{keyword}, '%')

SELECT
	contentSeq, contentType, title, exposure, contents, url, filePath, fileName,
	DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') regdate, DATE_FORMAT(moddate,'%Y-%m-%d %H:%i:%s') moddate
FROM TB_CONTENTS
WHERE contentSeq = #{contentSeq}
	AND contentType	= #{contentType}

mapper.update:
UPDATE TB_CONTENTS SET title = #{title}, exposure = #{exposure}, contents = #{contents}, url = #{url},
thumbUrl = #{thumbUrl}, filePath = #{filePath}, fileName = #{fileName},
moddate = now() WHERE contentSeq = ${contentSeq}
