mapper.select:
SELECT bannerSeq, title, exposure, url, filePath, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') regdate, DATE_FORMAT(moddate,'%Y-%m-%d %H:%i:%s') moddate FROM TB_BANNER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')
ORDER BY exposure DESC, regdate DESC LIMIT #{startIdx}, #{rowCount}

SELECT count(bannerSeq) cnt FROM TB_BANNER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')

SELECT chapterSeq, title, exposure, contents, url, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') regdate, DATE_FORMAT(moddate,'%Y-%m-%d %H:%i:%s') moddate FROM TB_EDB_CHAPTER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')
ORDER BY regdate DESC LIMIT #{startIdx}, #{rowCount}

SELECT count(chapterSeq) cnt FROM TB_EDB_CHAPTER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')

mapper.update:
UPDATE TB_EDB_CONTENT
SET
	contentOrder = #{contentOrder}
WHERE contentSeq = #{contentSeq}
	AND chapterSeq = #{chapterSeq}
