mapper.select:
SELECT bannerSeq, title, exposure, url, filePath, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') regdate, DATE_FORMAT(moddate,'%Y-%m-%d %H:%i:%s') moddate FROM TB_BANNER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')
ORDER BY exposure DESC, regdate DESC LIMIT #{startIdx}, #{rowCount}

SELECT count(bannerSeq) cnt FROM TB_BANNER WHERE 1=1
AND DATE(regdate) BETWEEN #{regFromDate} AND #{regToDate}
AND title like concat('%', #{keyword}, '%')
